import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";


//firebaseのstorageから画像を読み込んでくるコード

<script type="module" src="src/getPhoto.js"></script>

//https以外の画像ソースから表示可能か

export default function SimpleSlider() {
  const settings = {
    autoplay: true, //自動再生
    autoplaySpeed: 5000, //自動再生のスピード
    dots: true, //スライダー下の点の表示/非表示
    infinite: true, //無限スクロールの有効/無効
    speed: 1000, //スライド切り替えの速度
    slidesToShow: 1, //一度に表示するスライド数
    slidesToScroll: 1, //一度にスライドする枚数
  };
  return (
    <Slider {...settings}>
      <div className="photo1">
        <h3>1</h3>
        <img id="photo" src="" alt="Photo1"></img>
      </div>
      <div className="photo2">
        <h3>2</h3>
        <img id="photo" src="" alt="Photo1"></img>
      </div>
      <div className="photo3">
        <h3>3</h3>
        <img id="photo" src="" alt="Photo1"></img>
      </div>
      <div className="photo4">
        <h3>4</h3>
        <img id="photo" src="" alt="Photo1"></img>
      </div>
      <div className="photo5">
        <h3>5</h3>
        <img id="photo" src="" alt="Photo1"></img>
      </div>
    </Slider>
  );
}
